g.script:
(function(){
  var steps=96, Rin=200, Rout=220; var d='';
  for(var i=0;i<steps;i++){
    var a0=(i/steps)*Math.PI*2;
    var a1=((i+0.42)/steps)*Math.PI*2;
    var a2=((i+0.58)/steps)*Math.PI*2;
    var a3=((i+1)/steps)*Math.PI*2;
    var x0=Math.cos(a0)*Rin, y0=Math.sin(a0)*Rin;
    var x1=Math.cos(a1)*Rout, y1=Math.sin(a1)*Rout;
    var x2=Math.cos(a2)*Rout, y2=Math.sin(a2)*Rout;
    var x3=Math.cos(a3)*Rin, y3=Math.sin(a3)*Rin;
    if(i===0){ d+='M '+x0+' '+y0+' '; }
    d+='L '+x1+' '+y1+' L '+x2+' '+y2+' L '+x3+' '+y3+' ';
  }
  d+='Z';
  var p=document.getElementById('toothedRing');
  if(p) p.setAttribute('d', d);
})();
